import React, { useState } from "react";
import { TextField, Button, Dialog, DialogTitle, DialogContent, DialogContentText, DialogActions } from "@material-ui/core";
import "./Checkout.css";

const Checkout = (props) => {
  const [open, setOpen] = useState(false);
  const handleClose = () => {
    setOpen(false);
  };
  console.log(props);
  return (
    <div className="Checkout">
      <h2 id="chk-heading">Place Order</h2>
      <h3 id="chk-content">Order: {props.location.data?.orderString ?? "No Order"}</h3>
      <h3 id="chk-content">Total Price: ${props.location.data?.price ?? 0}</h3>
      <TextField color="secondary" required style={{ display: "inline-block", marginTop: 20, marginRight: 20 }} label="Name" />
      <TextField color="secondary" required style={{ display: "inline-block", marginTop: 20, marginRight: 20 }} label="Email" />
      <TextField color="secondary" required style={{ display: "inline-block", marginTop: 20 }} label="Building" />
      <TextField color="secondary" required fullWidth style={{ display: "block", marginTop: 20, marginRight: 100 }} label="Street" />
      <TextField color="secondary" required style={{ display: "inline-block", marginTop: 20, marginRight: 20 }} label="Pin Code" />
      <TextField color="secondary" required style={{ display: "inline-block", marginTop: 20, marginBottom: 20 }} label="Country" />
      <br></br>
      <div class="buttonGroup">
        <Button
          onClick={() => {
            props.history.push("/");
          }}
          style={{ marginTop: 40, marginRight: 25, display: "inline-block" }}
          id="checkout-button"
          variant="outlined"
          color="default"
        >
          Cancel
        </Button>
        <Button
          onClick={() => setOpen(true)}
          disabled={props.location.data === null || props.location?.data === undefined}
          style={{ marginTop: 40, display: "inline-block" }}
          id="checkout-button"
          variant="outlined"
          color="secondary"
        >
          Place Order
        </Button>
      </div>
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle id="responsive-dialog-title">Confirm Order</DialogTitle>
        <DialogContent>
          <DialogContentText>{props.location.data?.orderString ?? "No Order"}</DialogContentText>
          <DialogContentText>Are you sure you want you to place this order?</DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button autoFocus onClick={handleClose} color="secondary">
            Cancel
          </Button>
          <Button
            onClick={() => {
              setOpen(false);
              //   props.checkout(orderString, price);
            }}
            color="secondary"
            autoFocus
          >
            CONFIRM
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default Checkout;
