import React, { useState } from "react";
import "./SignUp.css";
import { TextField, Button, Dialog, DialogTitle, DialogContent, DialogContentText, DialogActions } from "@material-ui/core";
import CircularProgress from "@material-ui/core/CircularProgress";
import axios from "axios";

const SignUp = (props) => {
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);
  const [failure, setFailure] = useState(false);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confPassword, setConfPassword] = useState("");
  const signUpEndpoint = "accounts:signUp?key=AIzaSyDj7fGfYjQQDBvZkybj2h2MDY4AMP5BBlI";
  const updateAccEndpoint = "accounts:update?key=AIzaSyDj7fGfYjQQDBvZkybj2h2MDY4AMP5BBlI";

  const handleClose = () => {
    setLoading(false);
    setSuccess(false);
    setFailure(false);
  };

  const SignUpRequest = () => {
    setLoading(true);
    try {
      axios
        .post("https://identitytoolkit.googleapis.com/v1/" + signUpEndpoint, {
          email: email,
          password: password,
          returnSecureToken: true
        })
        .then((reponse) => {
          console.log(reponse);
          if (reponse.status === 200) {
            axios
              .post("https://identitytoolkit.googleapis.com/v1/" + updateAccEndpoint, {
                idToken: reponse.data.idToken,
                displayName: name,
                returnSecureToken: true
              })
              .then((resp) => {
                console.log(resp);
                if (resp.status === 200) {
                  setLoading(false);
                  setName("");
                  setEmail("");
                  setPassword("");
                  setConfPassword("");
                  setSuccess(true);
                } else {
                  setLoading(false);
                  setName("");
                  setEmail("");
                  setPassword("");
                  setConfPassword("");
                  setFailure(false);
                }
              });
          } else {
            setLoading(false);
            setName("");
            setEmail("");
            setPassword("");
            setConfPassword("");
            setFailure(false);
          }
        });
    } catch (error) {
      setLoading(false);
      setName("");
      setEmail("");
      setPassword("");
      setConfPassword("");
      setFailure(false);
    }
  };

  return (
    <div className="SignUp">
      <h2 id="chk-heading">Sign Up</h2>
      <TextField
        id="name"
        fullWidth
        onChange={(e) => {
          setName(e.target.value);
        }}
        value={name}
        color="secondary"
        required
        style={{ width: "75%", display: "block", marginTop: 35, marginRight: "auto", marginLeft: "auto" }}
        label="Name"
      />
      <TextField
        id="email"
        fullWidth
        onChange={(e) => {
          setEmail(e.target.value);
        }}
        value={email}
        color="secondary"
        required
        style={{ width: "75%", display: "block", marginTop: 25, marginRight: "auto", marginLeft: "auto" }}
        label="Email"
      />
      <TextField
        fullWidth
        value={password}
        type="password"
        onChange={(e) => {
          setPassword(e.target.value);
        }}
        color="secondary"
        required
        style={{ width: "75%", display: "block", marginTop: 25, marginRight: "auto", marginLeft: "auto" }}
        label="Password"
      />
      <TextField
        fullWidth
        type="password"
        value={confPassword}
        onChange={(e) => {
          setConfPassword(e.target.value);
        }}
        color="secondary"
        required
        style={{ width: "75%", display: "block", marginTop: 25, marginRight: "auto", marginLeft: "auto" }}
        label="Confirm Password"
      />
      <Button
        onClick={SignUpRequest}
        disabled={email === "" || password === "" || confPassword === "" || password !== confPassword}
        style={{ marginTop: 50, marginRight: "auto", marginLeft: "auto", display: "block" }}
        id="checkout-button"
        variant="outlined"
        color="secondary"
      >
        Sign UP
      </Button>
      <Dialog open={loading}>
        <CircularProgress style={{ padding: "50px" }} color="secondary" />
      </Dialog>
      <Dialog open={success} onClose={handleClose}>
        <DialogTitle id="responsive-dialog-title">Account Created</DialogTitle>
        <DialogContent>
          <DialogContentText>Your account was successfully created! Log In!</DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button
            autoFocus
            onClick={() => {
              handleClose();
              props.history.push("/login");
            }}
            color="secondary"
          >
            Ok
          </Button>
        </DialogActions>
      </Dialog>
      <Dialog open={failure} onClose={handleClose}>
        <DialogTitle id="responsive-dialog-title">Error</DialogTitle>
        <DialogContent>
          <DialogContentText>Somthing went wrong! Try again later</DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button
            autoFocus
            onClick={() => {
              handleClose();
            }}
            color="secondary"
          >
            Ok
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default SignUp;
