import React, { useState } from "react";
import "./SignUp.css";
import { TextField, Button, Dialog, DialogTitle, DialogContent, DialogContentText, DialogActions } from "@material-ui/core";
import axios from "axios";

const SignUp = (props) => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confPassword, setConfPassword] = useState("");
  const signUpEndpoint = "accounts:signUp?key=AIzaSyDj7fGfYjQQDBvZkybj2h2MDY4AMP5BBlI";
  const updateAccEndpoint = "accounts:update?key=AIzaSyDj7fGfYjQQDBvZkybj2h2MDY4AMP5BBlI";

  const SignUpRequest = () => {
    axios
      .post("https://identitytoolkit.googleapis.com/v1/" + signUpEndpoint, {
        email: email,
        password: password,
        returnSecureToken: true
      })
      .then((reponse) => {
        if (reponse.ok) {
          axios
            .post("https://identitytoolkit.googleapis.com/v1/" + updateAccEndpoint, {
              idToken: reponse.data.idToken,
              displayName: name,
              returnSecureToken: true
            })
            .then((resp) => {
              if (resp.ok) {
                console.log("SUCESS");
              } else {
                console.log("UPDATE ERROR");
              }
            });
        } else {
          console.log("USER REGISTRATION ERROR");
        }
      });
  };

  return (
    <div className="SignUp">
      <h2 id="chk-heading">Sign Up</h2>
      <TextField
        id="name"
        fullWidth
        onChange={(e) => {
          setName(e.target.value);
        }}
        value={name}
        color="secondary"
        required
        style={{ width: "75%", display: "block", marginTop: 35, marginRight: "auto", marginLeft: "auto" }}
        label="Name"
      />
      <TextField
        id="email"
        fullWidth
        onChange={(e) => {
          setEmail(e.target.value);
        }}
        value={email}
        color="secondary"
        required
        style={{ width: "75%", display: "block", marginTop: 25, marginRight: "auto", marginLeft: "auto" }}
        label="Email"
      />
      <TextField
        fullWidth
        value={password}
        type="password"
        onChange={(e) => {
          setPassword(e.target.value);
        }}
        color="secondary"
        required
        style={{ width: "75%", display: "block", marginTop: 25, marginRight: "auto", marginLeft: "auto" }}
        label="Password"
      />
      <TextField
        fullWidth
        type="password"
        value={confPassword}
        onChange={(e) => {
          setConfPassword(e.target.value);
        }}
        color="secondary"
        required
        style={{ width: "75%", display: "block", marginTop: 25, marginRight: "auto", marginLeft: "auto" }}
        label="Confirm Password"
      />
      <Button
        onClick={SignUpRequest}
        disabled={email === "" || password === "" || confPassword === "" || password !== confPassword}
        style={{ marginTop: 50, marginRight: "auto", marginLeft: "auto", display: "block" }}
        id="checkout-button"
        variant="outlined"
        color="secondary"
      >
        Sign UP
      </Button>
    </div>
  );
};

export default SignUp;
