import React, { useState } from "react";
import "./BurgerBuilder.css";
import Burger from "../../components/Burger/Burger";
import BurgerControls from "../BurgerControls/BurgerControls";
import axios from "../../axios-orders";

const BurgerBuilder = () => {
  const [lettuce, setLettuce] = useState(false);
  const [cheese, setCheese] = useState(false);
  const [meat, setMeat] = useState(false);

  const [loading, setLoading] = useState(false);
  const ingrediants = { lettuce, cheese, meat };

  const changeLettuce = () => setLettuce(!lettuce);
  const changeCheese = () => setCheese(!cheese);
  const changeMeat = () => setMeat(!meat);

  const handleCheckout = (orderString, price) => {
    const order = {
      ingrediants: ingrediants,
      orderString: orderString,
      price: price,
      customer: {
        name: "Dhananjayan P N",
        address: {
          house: "NCC Urban Green Province",
          street: "Sarjapur Road",
          pinCode: "562125",
          country: "India"
        },
        email: "pndjay@gmail.com"
      },
      deliveryMethod: "regular"
    };
    axios
      .post("/orders.json", order)
      .then((response) => {
        if (response.status === 200) {
          alert("Order successfully placed!");
        } else {
          console.log(response);
          alert("Something went wrong! Order not placed!");
        }
      })
      .catch((error) => {
        console.log(error);
        alert("Something went wrong! Order not placed!");
      });
  };

  return (
    <div className="BurgerBuilder">
      <Burger {...ingrediants} />
      <BurgerControls {...ingrediants} checkout={handleCheckout} changeLettuce={changeLettuce} changeCheese={changeCheese} changeMeat={changeMeat} />
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle id="responsive-dialog-title">Confirm Order</DialogTitle>
        <DialogContent>
          <DialogContentText>{orderString}</DialogContentText>
          <DialogContentText>Would you like to place this order?</DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button autoFocus onClick={handleClose} color="secondary">
            Cancel
          </Button>
          <Button
            onClick={() => {
              setOpen(false);
              props.checkout(orderString, price);
            }}
            color="secondary"
            autoFocus
          >
            PLACE ORDER
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default BurgerBuilder;
